CREATE OR REPLACE PROCEDURE pls_reajuste_pck.inserir_regra_via_adicional () AS $body$
BEGIN

if (tb_nr_seq_regra_via_cart_w.count > 0) then
	forall i in tb_nr_seq_regra_via_cart_w.first..tb_nr_seq_regra_via_cart_w.last
		insert into pls_regra_segurado_cart(	nr_sequencia, dt_atualizacao, nm_usuario,
				dt_atualizacao_nrec, nm_usuario_nrec, dt_inicio_vigencia,
				vl_via_adicional, nr_seq_contrato, nr_seq_reajuste,
				cd_estabelecimento, nr_via_inicial, nr_via_final,
				ie_renovacao, nr_seq_intercambio)
		values (	nextval('pls_regra_segurado_cart_seq'), clock_timestamp(), nm_usuario_p,
				clock_timestamp(), nm_usuario_p, dt_reajuste_p,
				tb_vl_via_adicional_w(i), tb_nr_seq_contrato_w(i), nr_seq_reajuste_p,
				cd_estabelecimento_p, tb_nr_via_inicial_w(i), tb_nr_via_final_w(i),
				tb_ie_renovacao_w(i), current_setting('pls_reajuste_pck.tb_nr_seq_intercambio_w')::pls_util_cta_pck.t_number_table(i))
		RETURNING nr_sequencia
		BULK COLLECT INTO tb_nr_seq_regra_via_adic_wr;
	commit;
	
	forall i in tb_nr_seq_regra_via_adic_wr.first..tb_nr_seq_regra_via_adic_wr.last
		update	pls_reajuste_via_cart
		set	nr_seq_regra_reajustada	= tb_nr_seq_regra_via_adic_wr(i)
		where	nr_sequencia		= tb_nr_seq_reaj_via_cart_w(i);
	commit;
	
	forall i in tb_nr_seq_regra_via_cart_w.first..tb_nr_seq_regra_via_cart_w.last
		update	pls_regra_segurado_cart
		set	dt_fim_vigencia = fim_dia(dt_reajuste_p-1)
		where	nr_sequencia = tb_nr_seq_regra_via_cart_w(i);
	commit;
end if;

CALL pls_reajuste_pck.limpar_vetor_inserir_via_adic();

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
