CREATE OR REPLACE PROCEDURE pls_rec_glosa_emp_pck.acatar_recursar_prot ( nr_seq_protocolo_p pls_grg_protocolo.nr_sequencia%type, ie_acao_p text, ds_justificativa_p pls_grg_guia_glosa.ds_justificativa%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

_ora2pg_r RECORD;
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Acata ou recursa o protocolo por completo, conforme parametro
	
	Os valores serao aplicados diretamente nos itens do protocolo, desde que os mesmos
	nao forem alterados diretamente. Neste caso e respeitado a decisao de "acatar / recursar"
	imposta pelo usuario a nivel de item

-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------

Pontos de atencao:

	
Alteracoes:
-------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */

-- tratamento de excecao

ds_msg_erro_w		varchar(4000):= '';
ds_callstack_erro_w	varchar(4000):= '';

-- tabelas virtuais

tb_nr_seq_item_w	dbms_sql.number_table;
tb_vl_acatado_w		dbms_sql.number_table;
tb_vl_recursado_w	dbms_sql.number_table;

-- Carrega os procedimentos

c01 CURSOR(	nr_seq_protocolo_pc	pls_grg_protocolo.nr_sequencia%type,
		ie_acao_pc		text) FOR
	SELECT	b.nr_sequencia,
		CASE WHEN ie_acao_pc='A' THEN  a.vl_glosado  ELSE 0.00 END  vl_acatado,
		CASE WHEN ie_acao_pc='R' THEN  a.vl_glosado  ELSE 0.00 END  vl_recursado
	from	pls_grg_guia_proc	a,	
		pls_grg_guia		b
	where	b.nr_sequencia		= a.nr_seq_grg_guia
	and	b.nr_seq_grg_protocolo	= nr_seq_protocolo_pc
	and	coalesce(a.ie_origem_acao::text, '') = ''
	and	(ie_acao_pc IS NOT NULL AND ie_acao_pc::text <> '');

-- Carrega os materiais

c02 CURSOR(	nr_seq_protocolo_pc	pls_grg_protocolo.nr_sequencia%type,
		ie_acao_pc		text) FOR
	SELECT	b.nr_sequencia,
		CASE WHEN ie_acao_pc='A' THEN  a.vl_glosado  ELSE 0.00 END  vl_acatado,
		CASE WHEN ie_acao_pc='R' THEN  a.vl_glosado  ELSE 0.00 END  vl_recursado
	from	pls_grg_guia_mat	a,	
		pls_grg_guia		b
	where	b.nr_sequencia		= a.nr_seq_grg_guia
	and	b.nr_seq_grg_protocolo	= nr_seq_protocolo_pc
	and	coalesce(a.ie_origem_acao::text, '') = ''
	and	(ie_acao_pc IS NOT NULL AND ie_acao_pc::text <> '');

BEGIN

-- Realiza as validacoes

CALL pls_rec_glosa_emp_pck.valida_acatar_recursar_prot(nr_seq_protocolo_p, ds_justificativa_p, ie_acao_p);
begin

	-- carrega os procedimentos

	open c01(nr_seq_protocolo_p, ie_acao_p);
	loop
	fetch c01 bulk collect into	tb_nr_seq_item_w,
					tb_vl_acatado_w,
					tb_vl_recursado_w limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_nr_seq_item_w.count = 0;
	
		SELECT * FROM pls_rec_glosa_emp_pck.grava_acatar_recursar_guia(	tb_nr_seq_item_w, tb_vl_acatado_w, tb_vl_recursado_w, 'P', 'P', nm_usuario_p, 'S', 'S') INTO STRICT _ora2pg_r;
 	tb_nr_seq_item_w := _ora2pg_r.tb_nr_seq_guia_p; tb_vl_acatado_w := _ora2pg_r.tb_vl_acatado_p; tb_vl_recursado_w := _ora2pg_r.tb_vl_recursado_p;
		
	end loop; -- fim loop procedimentos
	close c01;
	
	-- carrega os materiais

	open c02(nr_seq_protocolo_p, ie_acao_p);
	loop
	fetch c02 bulk collect into	tb_nr_seq_item_w,
					tb_vl_acatado_w,
					tb_vl_recursado_w limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_nr_seq_item_w.count = 0;
	
		SELECT * FROM pls_rec_glosa_emp_pck.grava_acatar_recursar_guia(	tb_nr_seq_item_w, tb_vl_acatado_w, tb_vl_recursado_w, 'P', 'M', nm_usuario_p, 'S', 'S') INTO STRICT _ora2pg_r;
 	tb_nr_seq_item_w := _ora2pg_r.tb_nr_seq_guia_p; tb_vl_acatado_w := _ora2pg_r.tb_vl_acatado_p; tb_vl_recursado_w := _ora2pg_r.tb_vl_recursado_p;
		
	end loop; -- fim loop Materiais
	close c02;
	
	-- Atualiza a justificativa das glosas

	update	pls_grg_protocolo_glosa
	set	ds_justificativa	= ds_justificativa_p
	where	nr_seq_grg_protocolo	= nr_seq_protocolo_p;
	
	commit;
	
exception
	when others then
	
		if (c01%isopen) then
			
			close c01;
		end if;
		
		if (c02%isopen) then
			
			close c02;
		end if;
		
		ds_msg_erro_w		:= sqlerrm;
		ds_callstack_erro_w	:= dbms_utility.format_error_backtrace;
		
		CALL wheb_mensagem_pck.exibir_mensagem_abort(843136,  'ERRO=' ||ds_msg_erro_w ||';CALL=' || ds_callstack_erro_w);
end;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
