CREATE OR REPLACE PROCEDURE pls_rec_glosa_emp_pck.atual_valores_rec_guia ( tb_nr_sequencia_p INOUT dbms_sql.number_table, tb_vl_acatado_p INOUT dbms_sql.number_table, tb_vl_recursado_p INOUT dbms_sql.number_table, tb_vl_glosado_p INOUT dbms_sql.number_table, nm_usuario_p usuario.nm_usuario%type, ie_commit_p text, ie_esvaziar_table_p text) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Atualiza o valor da glosa da guia no recurso

	Essa rotina apenas envia para o banco os valores, nao deve conter nenhuma 
	regra de negocio aqui
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------

Pontos de atencao:
Alteracoes:
*/


BEGIN

forall i in tb_nr_sequencia_p.first..tb_nr_sequencia_p.last
	update	pls_grg_guia
	set	vl_acatado	= tb_vl_acatado_p(i),
		vl_recursado	= tb_vl_recursado_p(i),
		vl_glosado	= tb_vl_glosado_p(i),
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= tb_nr_sequencia_p(i);
	
if (coalesce(ie_commit_p, 'S') = 'S') then

	commit;
end if;

if (coalesce(ie_esvaziar_table_p, 'N') = 'S') then

	tb_nr_sequencia_p.delete;
	tb_vl_acatado_p.delete;
	tb_vl_recursado_p.delete;
	tb_vl_glosado_p.delete;
	
end if;

END;

$body$
