CREATE OR REPLACE PROCEDURE pls_rec_glosa_emp_pck.desfazer_acatar_recursar_item ( nr_seq_grg_proc_p pls_grg_guia_proc.nr_sequencia%type, nr_seq_grg_mat_p pls_grg_guia_mat.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Desfaz as acoes e acatar ou recursar o item

-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------

Pontos de atencao:

	
Alteracoes:
-------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


nr_seq_grg_guia_w	pls_grg_guia.nr_sequencia%type;


BEGIN

-- Levanta a guia

select	max(t.nr_seq_grg_guia)
into STRICT	nr_seq_grg_guia_w
from (	SELECT	a.nr_seq_grg_guia
	from	pls_grg_guia_proc	a
	where	a.nr_sequencia		= nr_seq_grg_proc_p
	and	(nr_seq_grg_proc_p IS NOT NULL AND nr_seq_grg_proc_p::text <> '')
	
union all

	SELECT	a.nr_seq_grg_guia
	from	pls_grg_guia_mat	a
	where	a.nr_sequencia		= nr_seq_grg_mat_p
	and	(nr_seq_grg_mat_p IS NOT NULL AND nr_seq_grg_mat_p::text <> '')) t;

-- Realiza a validacao

CALL pls_rec_glosa_emp_pck.valida_desfazer_acat_rec(nr_seq_grg_guia_w);


-- se for passado um proc

if (nr_seq_grg_proc_p IS NOT NULL AND nr_seq_grg_proc_p::text <> '') then

	CALL pls_rec_glosa_emp_pck.atual_vl_acatado_rec_item(	nr_seq_grg_proc_p,
					'P',
					null,
					null,
					null,
					null, -- passa null para o vl glosado sem problemas, pq essa rotina nao atualiza ele
					null,
					nm_usuario_p);
	
	-- Recalcula o valor da guia

	CALL pls_rec_glosa_emp_pck.recalcula_vl_acat_rec_guia( nr_seq_grg_guia_w, nm_usuario_p);

end if;


-- se for passado um mat

if (nr_seq_grg_mat_p IS NOT NULL AND nr_seq_grg_mat_p::text <> '') then

	CALL pls_rec_glosa_emp_pck.atual_vl_acatado_rec_item(	nr_seq_grg_mat_p,
					'M',
					null,
					null,
					null,
					null, -- passa null para o vl glosado sem problemas, pq essa rotina nao atualiza ele
					null,
					nm_usuario_p);
	
	-- Recalcula o valor da guia

	CALL pls_rec_glosa_emp_pck.recalcula_vl_acat_rec_guia( nr_seq_grg_guia_w, nm_usuario_p);

end if;


END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
