CREATE OR REPLACE PROCEDURE pls_rec_glosa_emp_pck.desfazer_acatar_recursar_prot ( nr_seq_grg_protocolo_p pls_grg_protocolo.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Desfaz as acoes e acatar ou recursar o protocolo

	A validacao, por ser a nivel de guia, sera feita em um laco

-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------

Pontos de atencao:

	
Alteracoes:
-------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


-- Carrega as guias

c01 CURSOR(	nr_seq_grg_protocolo_pc	pls_grg_protocolo.nr_sequencia%type) FOR
	SELECT	a.nr_sequencia
	from	pls_grg_guia	a
	where	a.nr_seq_grg_protocolo	= nr_seq_grg_protocolo_pc;
	

BEGIN

-- Valida todas as guias de forma "Generica", nesta situacao, nao pode passar pela validacao de itens que a guia possuir

for r_c01_w in c01(nr_seq_grg_protocolo_p) loop

	CALL pls_rec_glosa_emp_pck.valida_desfazer_acat_rec(r_c01_w.nr_sequencia);
	CALL pls_rec_glosa_emp_pck.valida_desfazer_acat_rec_guia(r_c01_w.nr_sequencia);
end loop;


-- Varre a nivel de item

CALL pls_rec_glosa_emp_pck.acatar_recursar_prot(	nr_seq_grg_protocolo_p,
			null,
			null,
			nm_usuario_p);

commit;
-- Varre a nivel de guia

for r_c01_w in c01(nr_seq_grg_protocolo_p) loop

	CALL pls_rec_glosa_emp_pck.acatar_recursar_guia(	r_c01_w.nr_sequencia,
				null,
				null,
				nm_usuario_p);
end loop;

commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
