SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_rec_glosa_emp_pck.excluir_dados_rec_glosa ( nr_seq_grg_lote_p pls_grg_lote.nr_sequencia%type) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Exclui os dados no lote de envio de recurso de glosa

-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[]  Objetos do dicionario [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------

Pontos de atencao:
Alteracoes:
-------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


BEGIN

-- Faz a validacao

CALL pls_rec_glosa_emp_pck.validar_excluir_dados_rec(nr_seq_grg_lote_p);

-- Apaga os itens das guias

delete	FROM pls_erg_guia_item	x
where	exists (	SELECT	1
			from	pls_erg_recurso		a,
				pls_erg_cabecalho	b,
				pls_erg_guia		c
			where	b.nr_seq_erg_recurso	= a.nr_sequencia
			and	c.nr_seq_erg_cabecalho	= b.nr_sequencia
			and	a.nr_seq_grg_lote	= nr_seq_grg_lote_p
			and	c.nr_sequencia		= x.nr_seq_erg_guia);


-- As glosas das guias	

delete	FROM pls_erg_guia_glosa	x
where	exists (	SELECT	1
			from	pls_erg_recurso		a,
				pls_erg_cabecalho	b,
				pls_erg_guia		c
			where	b.nr_seq_erg_recurso	= a.nr_sequencia
			and	c.nr_seq_erg_cabecalho	= b.nr_sequencia
			and	a.nr_seq_grg_lote	= nr_seq_grg_lote_p
			and	c.nr_sequencia		= x.nr_seq_erg_guia);
			
-- As guias em si

delete	FROM pls_erg_guia	x
where	exists (	SELECT	1
			from	pls_erg_recurso		a,
				pls_erg_cabecalho	b
			where	b.nr_seq_erg_recurso	= a.nr_sequencia
			and	x.nr_seq_erg_cabecalho	= b.nr_sequencia
			and	a.nr_seq_grg_lote	= nr_seq_grg_lote_p);
			
-- Os Protocolos

delete	FROM pls_erg_protocolo	x
where	exists (	SELECT	1
			from	pls_erg_recurso		a,
				pls_erg_cabecalho	b
			where	b.nr_seq_erg_recurso	= a.nr_sequencia
			and	a.nr_seq_grg_lote	= nr_seq_grg_lote_p
			and	b.nr_sequencia		= x.nr_seq_erg_cabecalho);

-- Os prestadores

delete	FROM pls_erg_prestador	x
where	exists (	SELECT	1
			from	pls_erg_recurso		a,
				pls_erg_cabecalho	b
			where	b.nr_seq_erg_recurso	= a.nr_sequencia
			and	a.nr_seq_grg_lote	= nr_seq_grg_lote_p
			and	b.nr_sequencia		= x.nr_seq_erg_cabecalho);
			

-- O cabecalho

delete	FROM pls_erg_cabecalho	x
where	exists (	SELECT	1
			from	pls_erg_recurso		a
			where	x.nr_seq_erg_recurso	= a.nr_sequencia
			and	a.nr_seq_grg_lote	= nr_seq_grg_lote_p);

-- O lote em si

delete	FROM pls_erg_recurso	x
where	x.nr_seq_grg_lote	= nr_seq_grg_lote_p;


commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_rec_glosa_emp_pck.excluir_dados_rec_glosa ( nr_seq_grg_lote_p pls_grg_lote.nr_sequencia%type) FROM PUBLIC;
