SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_rec_glosa_emp_pck.excluir_recurso_demons ( nr_seq_demost_analise_p pls_dma_demons_analise.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, ie_commit_p text) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Exclui todos os lotes de recurso, que estiverem vinculados a um demonstrativo

-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[]  Objetos do dicionario [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------

Pontos de atencao:
Alteracoes:
-------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


BEGIN

-- limpa as glosas dos proc

delete	FROM pls_grg_guia_proc_glosa	x
where	exists (	SELECT	1
			from	pls_grg_lote		a,
				pls_grg_protocolo	b,
				pls_grg_guia		c,
				pls_grg_guia_proc	d
			where	b.nr_seq_grg_lote	= a.nr_sequencia
			and	c.nr_seq_grg_protocolo	= b.nr_sequencia
			and	d.nr_seq_grg_guia	= c.nr_sequencia
			and	d.nr_sequencia		= x.nr_seq_grg_proc
			and	a.nr_seq_dma_analise	= nr_seq_demost_analise_p);
			
-- limpa as glosas dos mat

delete	FROM pls_grg_guia_mat_glosa	x
where	exists (	SELECT	1
			from	pls_grg_lote		a,
				pls_grg_protocolo	b,
				pls_grg_guia		c,
				pls_grg_guia_mat	d
			where	b.nr_seq_grg_lote	= a.nr_sequencia
			and	c.nr_seq_grg_protocolo	= b.nr_sequencia
			and	d.nr_seq_grg_guia	= c.nr_sequencia
			and	d.nr_sequencia		= x.nr_seq_grg_mat
			and	a.nr_seq_dma_analise	= nr_seq_demost_analise_p);
			
-- limpa os proc

delete	FROM pls_grg_guia_proc	x
where	exists (	SELECT	1
			from	pls_grg_lote		a,
				pls_grg_protocolo	b,
				pls_grg_guia		c
			where	b.nr_seq_grg_lote	= a.nr_sequencia
			and	c.nr_seq_grg_protocolo	= b.nr_sequencia
			and	x.nr_seq_grg_guia	= c.nr_sequencia
			and	a.nr_seq_dma_analise	= nr_seq_demost_analise_p);
			
-- limpa os mat

delete	FROM pls_grg_guia_mat	x
where	exists (	SELECT	1
			from	pls_grg_lote		a,
				pls_grg_protocolo	b,
				pls_grg_guia		c
			where	b.nr_seq_grg_lote	= a.nr_sequencia
			and	c.nr_seq_grg_protocolo	= b.nr_sequencia
			and	x.nr_seq_grg_guia	= c.nr_sequencia
			and	a.nr_seq_dma_analise	= nr_seq_demost_analise_p);


-- limpa a glosa das guias

delete	FROM pls_grg_guia_glosa	x
where	exists (	SELECT	1
			from	pls_grg_lote		a,
				pls_grg_protocolo	b,
				pls_grg_guia		c
			where	b.nr_seq_grg_lote	= a.nr_sequencia
			and	c.nr_seq_grg_protocolo	= b.nr_sequencia
			and	x.nr_seq_grg_guia	= c.nr_sequencia
			and	a.nr_seq_dma_analise	= nr_seq_demost_analise_p);
			
-- limpa as guias

delete	FROM pls_grg_guia	x
where	exists (	SELECT	1
			from	pls_grg_lote		a,
				pls_grg_protocolo	b
			where	b.nr_seq_grg_lote	= a.nr_sequencia
			and	x.nr_seq_grg_protocolo	= b.nr_sequencia			
			and	a.nr_seq_dma_analise	= nr_seq_demost_analise_p);

-- limpa as glosas dos protocolos

delete	FROM pls_grg_protocolo_glosa	x
where	exists (	SELECT	1
			from	pls_grg_lote		a,
				pls_grg_protocolo	b				
			where	b.nr_seq_grg_lote	= a.nr_sequencia
			and	x.nr_seq_grg_protocolo	= b.nr_sequencia			
			and	a.nr_seq_dma_analise	= nr_seq_demost_analise_p);
			
-- limpa os protocolos

delete	FROM pls_grg_protocolo	x
where	exists (	SELECT	1
			from	pls_grg_lote		a				
			where	x.nr_seq_grg_lote	= a.nr_sequencia
			and	a.nr_seq_dma_analise	= nr_seq_demost_analise_p);
			
-- limpa os lotes de recurso

delete	FROM pls_grg_lote	x
where	x.nr_seq_dma_analise	= nr_seq_demost_analise_p;

CALL pls_rec_glosa_emp_pck.grava_dt_geracao_rec(nr_seq_demost_analise_p, null, nm_usuario_p, 'N');

if (coalesce(ie_commit_p, 'S') = 'S') then

	commit;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_rec_glosa_emp_pck.excluir_recurso_demons ( nr_seq_demost_analise_p pls_dma_demons_analise.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, ie_commit_p text) FROM PUBLIC;
