CREATE OR REPLACE PROCEDURE pls_rec_glosa_emp_pck.excluir_rpr_resposta ( nr_seq_rpr_resposta_p pls_rpr_resposta.nr_sequencia%type) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Apaga a resposta do recurso de glosa importada

-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------

Pontos de atencao:
Alteracoes:
-------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


BEGIN

-- Atualmente, nao e necessario validar alguma informacao para excluir. Caso futuramente isto mude, adicionar aqui uma rotina de validar.


-- Apaga os itens

delete	FROM pls_rpr_guia_item_imp	x
where	exists (	SELECT	1
			from	pls_rpr_resposta	a,
				pls_rpr_recibo_imp	b
			where	b.nr_seq_rpr_resposta	= a.nr_sequencia
			and	x.nr_seq_rpr_recibo	= b.nr_sequencia
			and	a.nr_sequencia		= nr_seq_rpr_resposta_p);
			
-- Apaga o prestador do recibo

delete	FROM pls_rpr_rec_prest_imp	x
where	exists (	SELECT	1
			from	pls_rpr_resposta	a,
				pls_rpr_recibo_imp	b
			where	b.nr_seq_rpr_resposta	= a.nr_sequencia
			and	x.nr_seq_rpr_recibo	= b.nr_sequencia
			and	a.nr_sequencia		= nr_seq_rpr_resposta_p);
			
-- Apaga a guia do recibo

delete	FROM pls_rpr_guia_imp	x
where	exists (	SELECT	1
			from	pls_rpr_resposta	a,
				pls_rpr_recibo_imp	b
			where	b.nr_seq_rpr_resposta	= a.nr_sequencia
			and	x.nr_seq_rpr_recibo	= b.nr_sequencia
			and	a.nr_sequencia		= nr_seq_rpr_resposta_p);

-- Apaga o protocolo do recibo

delete	FROM pls_rpr_protocolo_imp	x
where	exists (	SELECT	1
			from	pls_rpr_resposta	a,
				pls_rpr_recibo_imp	b
			where	b.nr_seq_rpr_resposta	= a.nr_sequencia
			and	x.nr_seq_rpr_recibo	= b.nr_sequencia
			and	a.nr_sequencia		= nr_seq_rpr_resposta_p);

-- Apaga o prestador do status

delete	FROM pls_rpr_status_prest_imp	x
where	exists (	SELECT	1
			from	pls_rpr_resposta	a,
				pls_rpr_status_imp	b
			where	b.nr_seq_rpr_resposta	= a.nr_sequencia
			and	x.nr_seq_rpr_status	= b.nr_sequencia
			and	a.nr_sequencia		= nr_seq_rpr_resposta_p);

-- Apaga o status			

delete	FROM pls_rpr_status_imp	x
where	x.nr_seq_rpr_resposta	= nr_seq_rpr_resposta_p;
			
-- Apaga mensagens de erro

delete	FROM pls_rpr_mensagem_erro	x
where	x.nr_seq_rpr_resposta	= nr_seq_rpr_resposta_p;

-- Apaga o recibo

delete	FROM pls_rpr_recibo_imp	x
where	x.nr_seq_rpr_resposta	= nr_seq_rpr_resposta_p;

-- Apaga a resposta

delete	FROM pls_rpr_resposta	x
where	x.nr_sequencia	= nr_seq_rpr_resposta_p;


commit;


END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
