SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_rec_glosa_emp_pck.gera_erg_guia_glosa ( nr_seq_erg_lote_p pls_erg_recurso.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, ie_commit_p text) AS $body$
DECLARE

_ora2pg_r RECORD;
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Gera as glosas das guias que sofrerao o recurso total
	
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------

Pontos de atencao:

	Para determinar quando uma guia pode ser enviada como um todo, ao inves de item a item,
	e olhado o tipo de recurso utilizado. Se foi recursado algum item em especifico da guia,
	mesmo que depois seja utilizado a opcao de recursar a guia inteira, sera considerado um
	recurso de item a item.
	
	Quando nao for recursado um valor em especifico da guia, apenas foi utilizada a opcao de 
	recurso de guia, entao sera gerado as glosas da guia.
	
	Para fins de negocios, ambos as formas podem recursar a guia inteira, nao apresentando perda.
		
Alteracoes:
-------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


-- tabelas virtuais

tb_nr_seq_erg_guia_w	dbms_sql.number_table;
tb_cd_glosa_w		dbms_sql.varchar2_table;
tb_ds_justificativa_w	dbms_sql.varchar2_table;

-- Carrega as guias

c01 CURSOR(	nr_seq_erg_lote_pc	pls_erg_recurso.nr_sequencia%type) FOR
	SELECT	c.nr_sequencia nr_seq_erg_guia,
		max(substr(tiss_obter_dados_motivo_glosa(e.nr_seq_motivo_glosa,'C'),1,255)) cd_glosa,
		max(e.ds_justificativa) ds_justificativa
	from	pls_erg_recurso		a,
		pls_erg_cabecalho	b,
		pls_erg_guia		c,
		pls_grg_guia		d,
		pls_grg_guia_glosa	e		
	where	b.nr_seq_erg_recurso	= a.nr_sequencia
	and	c.nr_seq_erg_cabecalho	= b.nr_sequencia
	and	d.nr_sequencia		= c.nr_seq_grg_guia
	and	e.nr_seq_grg_guia	= d.nr_sequencia
	and	e.nr_sequencia		= (	SELECT	max(x.nr_sequencia) nr_sequencia
						from	pls_grg_guia_glosa	x
						where	x.nr_seq_grg_guia	= d.nr_sequencia
						and	(x.ds_justificativa IS NOT NULL AND x.ds_justificativa::text <> ''))
	and (	select	count(1)
			from	pls_grg_guia_proc	x
			where	x.nr_seq_grg_guia	= c.nr_seq_grg_guia
			and	x.ie_origem_acao		= 'I') = 0
	and (	select	count(1)
			from	pls_grg_guia_mat	x
			where	x.nr_seq_grg_guia	= c.nr_seq_grg_guia
			and	x.ie_origem_acao		= 'I') = 0		
	and	a.nr_sequencia		= nr_seq_erg_lote_pc
	group by c.nr_sequencia;

BEGIN

begin

	-- carrega as glosas das guias que nao tiveram recursos a nivel de item

	open c01(nr_seq_erg_lote_p);
	loop
		fetch c01 bulk collect into	tb_nr_seq_erg_guia_w,
						tb_cd_glosa_w,
						tb_ds_justificativa_w limit pls_util_pck.qt_registro_transacao_w;
		exit when tb_nr_seq_erg_guia_w.count = 0;
		
		SELECT * FROM pls_rec_glosa_emp_pck.grava_erg_guia_glosa(	tb_nr_seq_erg_guia_w, tb_cd_glosa_w, tb_ds_justificativa_w, nm_usuario_p, 'N', 'S') INTO STRICT _ora2pg_r;
 	tb_nr_seq_erg_guia_w := _ora2pg_r.tb_nr_seq_erg_guia_p; tb_cd_glosa_w := _ora2pg_r.tb_cd_glosa_p; tb_ds_justificativa_w := _ora2pg_r.tb_ds_justificativa_p;
	end loop;


	if (coalesce(ie_commit_p, 'S') = 'S') then

		commit;
	end if;

exception
	when others then
	
		if (c01%isopen) then
		
			close c01;
		end if;
		rollback;
		CALL pls_rec_glosa_emp_pck.exibe_msg_erro_gerar_erg();

end;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_rec_glosa_emp_pck.gera_erg_guia_glosa ( nr_seq_erg_lote_p pls_erg_recurso.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, ie_commit_p text) FROM PUBLIC;
