CREATE OR REPLACE PROCEDURE pls_rec_glosa_emp_pck.valida_acatar_recursar_prot ( nr_seq_prot_p pls_grg_protocolo.nr_sequencia%type, ds_justificativa_p pls_grg_guia_glosa.ds_justificativa%type, ie_acao_p text) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Valida se e possivel acatar ou recursa o protocolo por completo.

	Essa rotina segue a modelagem de dados no Schema TISS, para evitar a geracao
	de informacao inconsistente com a modelagem.

-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------

Pontos de atencao:

	
Alteracoes:
-------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


qt_glosa_prot_w	integer;

BEGIN

-- se sera recursado

if (ie_acao_p = 'R') then

	-- Verifica se o protocolo possui ao menos uma glosa

	select	count(1)
	into STRICT	qt_glosa_prot_w
	from	pls_grg_protocolo_glosa	a
	where	a.nr_seq_grg_protocolo	= nr_seq_prot_p;

	if (coalesce(qt_glosa_prot_w,0) = 0) then

		CALL wheb_mensagem_pck.exibir_mensagem_abort(854687);
	end if;
	
	-- Se possui mensagem

	if (coalesce(ds_justificativa_p::text, '') = '') then
	
		CALL wheb_mensagem_pck.exibir_mensagem_abort(876886);
	end if;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
