CREATE OR REPLACE PROCEDURE pls_rec_glosa_emp_pck.valida_desfazer_acat_rec_guia ( nr_seq_grg_guia_p pls_grg_guia.nr_sequencia%type) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Valida se e possivel desfazer as acoes e acatar ou recursar a guia de forma generica

-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------

Pontos de atencao:

	
Alteracoes:
-------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


qt_itens_glosados_w	integer;


BEGIN

-- Validacao "Generica"

CALL pls_rec_glosa_emp_pck.valida_desfazer_acat_rec(nr_seq_grg_guia_p);

-- Valida se tem algum item com valor glosado

select	sum(t.contador)
into STRICT	qt_itens_glosados_w
from (	SELECT	count(1) contador
	from	pls_grg_guia_proc	a
	where	a.nr_seq_grg_guia	= nr_seq_grg_guia_p
	and	a.vl_glosado		> 0
	
union all

	SELECT	count(1) contador
	from	pls_grg_guia_mat	a
	where	a.nr_seq_grg_guia	= nr_seq_grg_guia_p
	and	a.vl_glosado		> 0) t;

if (coalesce(qt_itens_glosados_w, 0) > 0) then

	CALL wheb_mensagem_pck.exibir_mensagem_abort(876964);
end if;


END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
