CREATE OR REPLACE PROCEDURE pls_rec_glosa_emp_pck.valida_excluir_dma_imp ( nr_seq_demost_analise_p pls_dma_demons_analise.nr_sequencia%type) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Exclui o demonstrativo de analise importado

-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[]  Objetos do dicionario [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------

Pontos de atencao:
Alteracoes:
-------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


qt_lote_recurso_w	integer;
dt_geracao_rec_w	pls_dma_demons_analise.dt_geracao_rec%type;


BEGIN

-- Verifica a data de geracao

select	max(a.dt_geracao_rec)
into STRICT	dt_geracao_rec_w
from	pls_dma_demons_analise	a
where	a.nr_sequencia		= nr_seq_demost_analise_p;

-- se possuir a data, nao permite

if (dt_geracao_rec_w IS NOT NULL AND dt_geracao_rec_w::text <> '') then

	-- O demonstrativo ja foi gerado

	CALL wheb_mensagem_pck.exibir_mensagem_abort(868713);
end if;


-- Verifica se nao tem nenhum lote ja gerado

select	count(1)
into STRICT	qt_lote_recurso_w
from	pls_grg_lote	a
where	a.nr_seq_dma_analise	= nr_seq_demost_analise_p;

if (coalesce(qt_lote_recurso_w,0) > 0) then

	-- O demonstrativo ja possui lote de recurso

	CALL wheb_mensagem_pck.exibir_mensagem_abort(868718);
end if;


END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
