CREATE OR REPLACE PROCEDURE pls_rec_glosa_emp_pck.validar_excluir_dados_rec ( nr_seq_grg_lote_p pls_grg_lote.nr_sequencia%type) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Valida se pode excluir os dados gerados para o xml de rec glosa

	Se tiver um unico recurso fechado, nao podera excluir os demais
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[]  Objetos do dicionario [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------

Pontos de atencao:
Alteracoes:
-------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


qt_recurso_arquivo_w	integer;

BEGIN

select	count(1)
into STRICT	qt_recurso_arquivo_w
from	pls_erg_recurso		a
where	a.nr_seq_grg_lote	= nr_seq_grg_lote_p
and	(a.dt_geracao_arquivo IS NOT NULL AND a.dt_geracao_arquivo::text <> '');


if (coalesce(qt_recurso_arquivo_w,0) > 0) then
	
	CALL wheb_mensagem_pck.exibir_mensagem_abort(857203);
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
