SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_rec_glosa_emp_pck.validar_geracao_lote_rec ( nr_seq_demost_analise_p pls_dma_demons_analise.nr_sequencia%type) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Realiza as validacoes inciais, que determinarao se o lote podera ser gerado 

-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------

Pontos de atencao:
Alteracoes:
-------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


qt_demonstrativo_w	integer;
nr_seq_lote_w		pls_grg_lote.nr_sequencia%type;


BEGIN

-- Verifica se o demonstrativo informa e valido

select	count(1)
into STRICT	qt_demonstrativo_w
from	pls_dma_demons_analise
where	nr_sequencia	= nr_seq_demost_analise_p;

if (qt_demonstrativo_w = 0) then
	
	CALL wheb_mensagem_pck.exibir_mensagem_abort(843132);
end if;


-- Verifica se o demonstrativo ja nao foi incluso em outro lote.

select	max(a.nr_sequencia)
into STRICT	nr_seq_lote_w
from	pls_grg_lote		a
where	a.nr_seq_dma_analise	= nr_seq_demost_analise_p;

if (nr_seq_lote_w IS NOT NULL AND nr_seq_lote_w::text <> '') then

	CALL wheb_mensagem_pck.exibir_mensagem_abort(843133, 'LOTE='||to_char(nr_seq_lote_w));
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
