CREATE OR REPLACE PROCEDURE pls_recalcular_copartic_pck.pls_marcar_para_estorno ( nr_seq_conta_p pls_conta.nr_sequencia%type, nr_seq_lote_recalc_p pls_recalc_copartic_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
