CREATE OR REPLACE PROCEDURE pls_recalcular_copartic_pck.pls_recalc_copartic_contas ( nr_seq_lote_recalc_p pls_recalc_copartic_lote.nr_sequencia%type, nr_seq_conta_p pls_conta.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


nr_seq_conta_coparticipacao_w	pls_conta_coparticipacao.nr_sequencia%type;
nr_seq_lote_copartic_w		pls_lote_coparticipacao.nr_sequencia%type;
nr_seq_recalc_conta_w		pls_recalc_copartic_conta.nr_sequencia%type;
nm_beneficiario_w		varchar(255);
t_recalc_coparticipacao_ww	t_recalc_coparticipacao_row;
qt_recalc_copartic_w		integer;
nr_seq_lote_ted_w		pls_lote_ted_copartic.nr_sequencia%type;
ie_status_lote_copartic_w	pls_lote_coparticipacao.ie_status%type;


BEGIN

	select	max(nr_sequencia)
	into STRICT	nr_seq_conta_coparticipacao_w
	from	pls_conta_coparticipacao
	where	nr_seq_conta = nr_seq_conta_p;

	select	max(c.nr_seq_lote)
	into STRICT	nr_seq_lote_ted_w
	from	pls_ted_copartic_item	a,
		pls_ted_copartic_benef	b,
		pls_ted_copartic_pagador c
	where	b.nr_sequencia	= a.nr_seq_ted_benef
	and	c.nr_sequencia	= b.nr_seq_ted_pagador
	and	a.nr_seq_conta_copartic	= nr_seq_conta_coparticipacao_w;

	if (nr_seq_lote_ted_w IS NOT NULL AND nr_seq_lote_ted_w::text <> '') then -- N?o ? poss?vel realizar o rec?lculo, pois j? existe coparticipa??o vinculada ao lote #@NR_SEQ_LOTE#@ na fun??o OPS - Transfer?ncia Eletr?nica de Dados (TED) / Controle de coparticipa??es
		CALL wheb_mensagem_pck.exibir_mensagem_abort(336374,'NR_SEQ_LOTE='||nr_seq_lote_ted_w);
	end if;

	select	max(nr_sequencia)
	into STRICT 	nr_seq_recalc_conta_w
	from	pls_recalc_copartic_conta
	where	nr_seq_conta 	= nr_seq_conta_p
	and	nr_seq_lote	= nr_seq_lote_recalc_p;

	select	max(nr_seq_lote)
	into STRICT	nr_seq_lote_copartic_w
	from	pls_lib_coparticipacao
	where	nr_seq_conta_coparticipacao = nr_seq_conta_coparticipacao_w;

	ie_status_lote_copartic_w	:= 'X';

	if (nr_seq_lote_copartic_w IS NOT NULL AND nr_seq_lote_copartic_w::text <> '') then
		select 	ie_status
		into STRICT	ie_status_lote_copartic_w
		from	pls_lote_coparticipacao
		where	nr_sequencia = nr_seq_lote_copartic_w;
	end if;

	--aaschlote 30/03/2016 OS 1044687 - Caso existir um lote de coparticipa??o para o item com status definitivo ent?o n?o insere na tabela e n?o apaga a integridade
	if (ie_status_lote_copartic_w <> 'D') then
		update  pls_lib_coparticipacao
		set	nr_seq_conta_coparticipacao  = NULL
		where	nr_seq_conta_coparticipacao = nr_seq_conta_coparticipacao_w;

		-- Limpa a coparticipa??o que ser? exclu?da se j? existir em um lote de recalc?lo
		update	pls_recalc_coparticipacao
		set	nr_seq_conta_coparticipacao 	 = NULL,
			nm_usuario			= nm_usuario_p,
			dt_atualizacao			= clock_timestamp()
		where	nr_seq_conta_coparticipacao 	= nr_seq_conta_coparticipacao_w;

		-- Se n?o existir esta conta no lote ent?o insere a mesma.
		if (coalesce(nr_seq_recalc_conta_w::text, '') = '') then

			insert into pls_recalc_copartic_conta(nr_sequencia, nr_seq_lote, nr_seq_conta,
						nm_usuario_nrec, nm_usuario, dt_atualizacao_nrec,
						dt_atualizacao)
			values (nextval('pls_recalc_copartic_conta_seq'), nr_seq_lote_recalc_p, nr_seq_conta_p,
				nm_usuario_p, nm_usuario_p, clock_timestamp(),
				clock_timestamp()) returning nr_sequencia into nr_seq_recalc_conta_w;
		end if;
	end if;
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
