CREATE OR REPLACE PROCEDURE pls_reembolso_pck.grava_agrupamento_lote_titulo (nr_id_transacao_p pls_lote_titulo_reembolso.nr_sequencia%type) AS $body$
DECLARE


C01 CURSOR(	nr_id_transacao_p	pls_lote_titulo_reembolso.nr_sequencia%type) FOR
	SELECT	min(nr_sequencia) nr_lote,
		cd_pessoa_fisica,
		cd_cgc
	from	pls_lote_titulo_reembolso
	where	nr_id_transacao = nr_id_transacao_p
	group by cd_pessoa_fisica, cd_cgc;

BEGIN

for r_c01_w in C01(nr_id_transacao_p) loop
	if (r_c01_w.cd_pessoa_fisica IS NOT NULL AND r_c01_w.cd_pessoa_fisica::text <> '') and (r_c01_w.cd_cgc IS NOT NULL AND r_c01_w.cd_cgc::text <> '') then
		update	pls_lote_titulo_reembolso
		set	nr_lote			= r_c01_w.nr_lote
		where	nr_id_transacao		= nr_id_transacao_p
		and	cd_pessoa_fisica	= r_c01_w.cd_pessoa_fisica
		and	cd_cgc			= r_c01_w.cd_cgc;
	end if;
	
	if (r_c01_w.cd_pessoa_fisica IS NOT NULL AND r_c01_w.cd_pessoa_fisica::text <> '') and (coalesce(r_c01_w.cd_cgc::text, '') = '') then
		update	pls_lote_titulo_reembolso
		set	nr_lote			= r_c01_w.nr_lote
		where	nr_id_transacao		= nr_id_transacao_p
		and	cd_pessoa_fisica	= r_c01_w.cd_pessoa_fisica
		and	coalesce(cd_cgc::text, '') = '';
	end if;
	
	if (coalesce(r_c01_w.cd_pessoa_fisica::text, '') = '') and (r_c01_w.cd_cgc IS NOT NULL AND r_c01_w.cd_cgc::text <> '') then
		update	pls_lote_titulo_reembolso
		set	nr_lote			= r_c01_w.nr_lote
		where	nr_id_transacao		= nr_id_transacao_p
		and	cd_cgc			= r_c01_w.cd_cgc
		and	coalesce(cd_pessoa_fisica::text, '') = '';
	end if;
end loop;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
