CREATE OR REPLACE PROCEDURE pls_regra_qtd_execucao_pck.pls_gerencia_regra_qtd_exec ( nr_seq_lote_p pls_lote_protocolo_conta.nr_sequencia%type, nr_seq_protocolo_p pls_protocolo_conta.nr_sequencia%type, nr_seq_lote_processo_p pls_cta_lote_processo.nr_sequencia%type, nr_seq_conta_p pls_conta.nr_sequencia%type, nr_seq_conta_proc_p pls_conta_proc.nr_sequencia%type, nr_seq_analise_p pls_analise_conta.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type ) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Esta procedure é responsável por fazer o processamento das regras de quantidade de execução e a abertura de procedimentos.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_conta_lote_w  integer;

BEGIN
-- se for por processo por lote alimenta tabela temporária
if (nr_seq_lote_processo_p IS NOT NULL AND nr_seq_lote_processo_p::text <> '') then
  -- se não tiver registros na tabela manda gerar
  select  count(1)
  into STRICT  qt_conta_lote_w
  from  pls_cta_lote_proc_cta_temp;

  if (qt_conta_lote_w = 0) then
    CALL gera_cta_temp_lote_processo(nr_seq_lote_processo_p);
  end if;
end if;

CALL pls_regra_qtd_execucao_pck.pls_limpa_qtd_exec(  nr_seq_lote_p, nr_seq_protocolo_p, nr_seq_lote_processo_p,
      nr_seq_conta_p, nr_seq_conta_proc_p, nr_seq_analise_p,
      nm_usuario_p, cd_estabelecimento_p);

-- vincular as regras de quantidade de execução aos procedimentos (OPS - Regras e critérios de preço, pasta Regras ->
CALL pls_regra_qtd_execucao_pck.pls_vincular_regras_qtd_exec(  nr_seq_lote_p, nr_seq_protocolo_p, nr_seq_lote_processo_p,
        nr_seq_conta_p, nr_seq_conta_proc_p, nr_seq_analise_p,
        nm_usuario_p, cd_estabelecimento_p);

CALL pls_regra_qtd_execucao_pck.pls_abrir_proc_qtd_exec(  nr_seq_lote_p, nr_seq_protocolo_p, nr_seq_lote_processo_p,
        nr_seq_conta_p, nr_seq_conta_proc_p, nr_seq_analise_p,
        nm_usuario_p, cd_estabelecimento_p);

END;

$body$
