CREATE OR REPLACE FUNCTION pls_regra_qtd_execucao_pck.pls_obter_regra_tx ( nr_seq_regra_p pls_regra_preco_tx_proc.nr_seq_regra%type, qt_executada_p integer) RETURNS bigint AS $body$
DECLARE


nr_seq_regra_tx_w  pls_regra_preco_tx_proc.nr_sequencia%type;


BEGIN
-- busca a regra da taxa
select  max(nr_sequencia)
into STRICT  nr_seq_regra_tx_w
from   pls_regra_preco_tx_proc
where   nr_seq_regra = nr_seq_regra_p
and     ie_situacao = 'A'
and   qt_executada_p between qt_exec_min and coalesce(qt_exec_max, 9999999999);

return nr_seq_regra_tx_w;

END;

$body$
