CREATE OR REPLACE PROCEDURE pls_regra_via_acesso_pck.pls_aplica_regra_proc_via ( nr_seq_conta_proc_p dbms_sql.number_table, nr_seq_regra_p dbms_sql.number_table, ie_via_acesso_p dbms_sql.varchar2_table, tx_item dbms_sql.number_table ) AS $body$
DECLARE

/* ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Rotina responsavel por vincular dados referentes a via de acesso aos procedimentos.
+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
	

BEGIN

if (nr_seq_conta_proc_p.count > 0) then
	forall i in nr_seq_conta_proc_p.first..nr_seq_conta_proc_p.last
		update	pls_conta_proc
		set	ie_via_acesso		= ie_via_acesso_p(i),
			tx_item			= tx_item(i),
			nr_seq_regra_via_acesso	= nr_seq_regra_p(i)
		where	nr_sequencia		= nr_seq_conta_proc_p(i)
		and 	((coalesce(ie_via_acesso_manual::text, '') = '') or (ie_via_acesso_manual = 'N'))
		and	((coalesce(ie_tx_manual::text, '') = '') or (ie_tx_manual	= 'N'));			

	commit;		
end if;

END;

$body$
