CREATE OR REPLACE PROCEDURE pls_regra_via_acesso_pck.pls_atualiza_obrig_via_acesso ( nr_seq_conta_proc_p dbms_sql.number_table, nr_seq_regra_via_p dbms_sql.number_table) AS $body$
DECLARE

/* ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: 	Rotina responsavel por alterar o campo IE_VIA_OBRIGATORIA de cada procedimento para 'S'.
	(Obs: o campo IE_VIA_OBRIGATORIA indica se a via de acesso e obrigatoria para o procedimento)
+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
				

BEGIN

if (nr_seq_conta_proc_p.count > 0) then

	forall i in nr_seq_conta_proc_p.first..nr_seq_conta_proc_p.last		
		update	pls_conta_proc
		set	ie_via_obrigatoria	= 'S'
		where	nr_sequencia		= nr_seq_conta_proc_p(i);
	
	
	forall i in nr_seq_conta_proc_p.first..nr_seq_conta_proc_p.last		
		update	pls_conta_proc_regra
		set	nr_seq_regra_via_obrig	= nr_seq_regra_via_p(i)
		where	nr_sequencia		= nr_seq_conta_proc_p(i);
		
	commit;
end if;
	
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
