CREATE OR REPLACE PROCEDURE pls_regra_via_acesso_pck.pls_atualiza_proc_ref ( nr_seq_conta_proc_p dbms_sql.number_table, nr_seq_proc_ref_p dbms_sql.number_table) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Rotina responsavel por atualizar os procedimentos com as novas referencias.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
				

BEGIN

if (nr_seq_conta_proc_p.count > 0) then
	forall i in nr_seq_conta_proc_p.first..nr_seq_conta_proc_p.last
		update	pls_conta_proc proc
		set	nr_seq_proc_ref	= nr_seq_proc_ref_p(i)
		where	nr_sequencia	= nr_seq_conta_proc_p(i)
		and	not exists (SELECT	1
					from 	pls_conta_proc procref
					where	procref.nr_seq_proc_ref		= proc.nr_sequencia
					and	procref.cd_procedimento		= proc.cd_procedimento
					and	procref.ie_origem_proced	= proc.ie_origem_proced);
				
	commit;
end if;
	
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
