CREATE OR REPLACE PROCEDURE pls_regra_via_acesso_pck.pls_define_proc_referencia ( nr_seq_lote_p pls_lote_protocolo_conta.nr_sequencia%type, nr_seq_protocolo_p pls_protocolo_conta.nr_sequencia%type, nr_seq_lote_processo_p pls_cta_lote_processo.nr_sequencia%type, nr_seq_conta_p pls_conta.nr_sequencia%type, nr_seq_conta_proc_p pls_conta_proc.nr_sequencia%type, nr_seq_analise_p pls_conta.nr_seq_analise%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type ) AS $body$
DECLARE

_ora2pg_r RECORD;
/* ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: 	Rotina responsavel por armazenar todos os procedimentos junto a seus novos procedimentos de referencia para que possam 
	ser atualizados na procedure PLS_ATUALIZA_PROC_REF.
+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 	

i				integer;
qt_registro_transacao_w		integer;
qt_conta_lote_w			integer;

nr_seq_conta_proc_table_w   	dbms_sql.number_table;
nr_seq_proc_ref_table_w	    	dbms_sql.number_table;	
BEGIN

-- se for utilizar o lote de procedimento usa a tabela temporaria porque a original tem muita desfragmentacao devido a natureza do processo
if (nr_seq_lote_processo_p IS NOT NULL AND nr_seq_lote_processo_p::text <> '') then
	-- se nao tiver registros na tabela manda gerar
	select	count(1)
	into STRICT	qt_conta_lote_w
	from	pls_cta_lote_proc_cta_temp;

	if (qt_conta_lote_w = 0) then
		CALL gera_cta_temp_lote_processo(nr_seq_lote_processo_p);
	end if;
end if;

i := 0;

-- Obtem o controle padrao da quantidade de registros que sera enviada a cada vez para o banco de dados
qt_registro_transacao_w := pls_util_cta_pck.qt_registro_transacao_w;

-- Cursor de procedimentos que retorna somente a guia de referencia e a sequencia do segurado
for r_C01_w in current_setting('pls_regra_via_acesso_pck.c_dados_proc_param')::CURSOR((nr_seq_lote_p, nr_seq_protocolo_p, nr_seq_lote_processo_p, nr_seq_conta_p, nr_seq_conta_proc_p, nr_seq_analise_p) loop	
	-- Cursor de procedimentos restringido somente pela guia de referencia e sequencia do segurado
	for r_C02_w in current_setting('pls_regra_via_acesso_pck.c_dados_proc_ref')::CURSOR((r_C01_w.cd_guia_referencia, r_C01_w.nr_seq_segurado) loop	
		begin		
		
		-- Obtem os procedimentos junto aos novos procedimentos de referencia
		SELECT * FROM pls_regra_via_acesso_pck.pls_obtem_novo_proc_ref(r_C02_w, nm_usuario_p, cd_estabelecimento_p, i, nr_seq_conta_proc_table_w, nr_seq_proc_ref_table_w) INTO STRICT _ora2pg_r;
 i := _ora2pg_r.i_p; nr_seq_conta_proc_table_w := _ora2pg_r.nr_seq_conta_proc_table_p; nr_seq_proc_ref_table_w := _ora2pg_r.nr_seq_proc_ref_table_p;	
		
		if (i >= 1 ) then	
			-- Vincula as novas referencias aos procedimentos
			CALL pls_regra_via_acesso_pck.pls_atualiza_proc_ref(nr_seq_conta_proc_table_w, nr_seq_proc_ref_table_w);
			
			-- Limpa as variaveis table
			nr_seq_conta_proc_table_w.delete;
			nr_seq_proc_ref_table_w.delete;
			
			i := 0;
		end if;	
		end;
	end loop;	
end loop;

-- Caso sobre algum procedimento dentro das tables, estes tambem devem ser atualizados
CALL pls_regra_via_acesso_pck.pls_atualiza_proc_ref(nr_seq_conta_proc_table_w, nr_seq_proc_ref_table_w);

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
