CREATE OR REPLACE PROCEDURE pls_regra_via_acesso_pck.pls_recalcula_valorizacao ( nr_seq_lote_p pls_lote_protocolo_conta.nr_sequencia%type, nr_seq_protocolo_p pls_protocolo_conta.nr_sequencia%type, nr_seq_lote_processo_p pls_cta_lote_processo.nr_sequencia%type, nr_seq_conta_p pls_conta.nr_sequencia%type, nr_seq_conta_proc_p pls_conta_proc.nr_sequencia%type, nr_seq_analise_p pls_conta.nr_seq_analise%type, cd_acao_analise_p pls_acao_analise.cd_acao%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type ) AS $body$
DECLARE
	
/* ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Rotina resonsavel por definir quais contas terao sua valorizacao recalculada.
+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
			
qt_regra_via_acesso_obr_w	integer;
ie_recalcula_valorizacao_w	varchar(1);

BEGIN

-- Verifica se existe alguma regra de via de acesso obrigatoria ativa
select	count(1)
into STRICT	qt_regra_via_acesso_obr_w
from	pls_regra_via_acesso
where	ie_situacao = 'A';

if (qt_regra_via_acesso_obr_w > 0) then
	for r_C01_w in pls_util_cta_pck.conta_geral(nr_seq_lote_p, nr_seq_protocolo_p, nr_seq_lote_processo_p, nr_seq_conta_p, nr_seq_conta_proc_p, nr_seq_analise_p) loop		
		if (cd_acao_analise_p in (1, 2, 3, 4, 5)) then -- 1 = Ajustar o valor do item / 2 = Alterar valor Tx intercambio / 3 = Aceitar valor calculado / 4 = Aceitar valor apresentado / 5 = Aceitar melhor valor
			ie_recalcula_valorizacao_w := 'S';
		else
			ie_recalcula_valorizacao_w := 'N';
		end if;	

		if (ie_recalcula_valorizacao_w = 'S') then
			CALL pls_recalcular_conta(r_C01_w.nr_seq_conta, nm_usuario_p, 'G', 'S','N',null,null);
			CALL pls_atualiza_lib_conta(r_C01_w.nr_seq_conta, 'A', nm_usuario_p);
		end if;
	end loop;
	null;
end if;	

commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
