CREATE OR REPLACE FUNCTION pls_relatorio_ops_pck.imprimir_relat_3030_tabela ( nr_seq_tabela_prin_p pls_tabela_preco.nr_sequencia%type, nr_seq_tabela_aux_p pls_tabela_preco.nr_sequencia%type) RETURNS SETOF T_RELATORIO_3030_TAB_DATA AS $body$
DECLARE


	t_relatorio_3030_tab_w		t_relatorio_3030_tab_row;
	nr_seq_plano_preco_aux_w	pls_plano_preco.nr_sequencia%type;
	vl_preco_atual_w		pls_plano_preco.vl_preco_atual%type;
	qt_idade_inicial_w		pls_plano_preco.qt_idade_inicial%type;


	C01 CURSOR(	nr_seq_tabela_princ_pc	pls_tabela_preco.nr_sequencia%type) FOR
		SELECT	b.vl_preco_atual,
			b.qt_idade_inicial,
			b.qt_idade_final,
			a.nr_seq_plano
		from	pls_plano_preco		b,
			pls_tabela_preco	a
		where	b.nr_seq_tabela		= a.nr_sequencia
		and	a.nr_sequencia		= nr_seq_tabela_princ_pc;

	
BEGIN

	for r_c01_w in C01(nr_seq_tabela_prin_p) loop
		begin

		t_relatorio_3030_tab_w.nr_seq_plano_princ 	:= r_c01_w.nr_seq_plano;
		t_relatorio_3030_tab_w.qt_faixa_etaria_prin	:= r_c01_w.qt_idade_inicial;
		t_relatorio_3030_tab_w.vl_mensalidade_prin 	:= r_c01_w.vl_preco_atual;

		select	max(nr_sequencia)
		into STRICT	nr_seq_plano_preco_aux_w
		from	pls_plano_preco
		where	nr_seq_tabela		= nr_seq_tabela_aux_p
		and	qt_idade_inicial	= r_c01_w.qt_idade_inicial
		and	qt_idade_final		= r_c01_w.qt_idade_final;

		if (nr_seq_plano_preco_aux_w IS NOT NULL AND nr_seq_plano_preco_aux_w::text <> '') then
			select	vl_preco_atual,
				qt_idade_inicial
			into STRICT	vl_preco_atual_w,
				qt_idade_inicial_w
			from	pls_plano_preco
			where	nr_sequencia		= nr_seq_plano_preco_aux_w;

			t_relatorio_3030_tab_w.nr_seq_plano_aux 	:= r_c01_w.nr_seq_plano;
			t_relatorio_3030_tab_w.qt_faixa_etaria_aux	:= qt_idade_inicial_w;
			t_relatorio_3030_tab_w.vl_mensalidade_aux 	:= vl_preco_atual_w;
		end if;
		if (t_relatorio_3030_tab_w.vl_mensalidade_aux <> t_relatorio_3030_tab_w.vl_mensalidade_prin) then
			RETURN NEXT t_relatorio_3030_tab_w;
		end if;
		end;
	end loop;

	return;
	end;


$body$
