----------------INICIO DO PROCESSO DESFAZER O LOTE----------------------
CREATE OR REPLACE PROCEDURE pls_repasse_sca_pck.desfazer_lote_sca ( nr_seq_lote_p pls_repasse_sca_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
BEGIN

delete	from pls_repasse_sca_benef_med
where	nr_seq_repasse_benef in (SELECT		a.nr_sequencia
			from	pls_repasse_sca_benef a,
				pls_repasse_sca_plano b,
				pls_repasse_sca_fornecedor c
			where	c.nr_seq_lote	= nr_seq_lote_p
			and	c.nr_sequencia	= b.nr_seq_repasse_fornecedor
			and	b.nr_sequencia	= a.nr_seq_repasse_plano);

delete	from pls_repasse_sca_benef
where	nr_seq_repasse_plano in (SELECT		a.nr_sequencia
			from	pls_repasse_sca_plano a,
				pls_repasse_sca_fornecedor b
			where	b.nr_seq_lote	= nr_seq_lote_p
			and	b.nr_sequencia	= a.nr_seq_repasse_fornecedor);

delete	from pls_repasse_sca_plano
where	nr_seq_repasse_fornecedor in (SELECT	a.nr_sequencia
			from	pls_repasse_sca_fornecedor a
			where	a.nr_seq_lote	= nr_seq_lote_p);

delete	from pls_repasse_sca_fornecedor
where	nr_seq_lote = nr_seq_lote_p;

update	pls_repasse_sca_lote
set	dt_geracao_lote	 = NULL,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia = nr_seq_lote_p;

commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
