CREATE OR REPLACE PROCEDURE pls_resumo_prop_online_pck.add_sca ( nr_seq_proposta_online_p pls_proposta_online.nr_sequencia%type, nr_seq_benef_p pls_proposta_benef_online.nr_sequencia%type, qt_idade_p bigint, ie_tipo_benef_p text) AS $body$
DECLARE

				
	vl_sca_w	double precision;
	qt_vidas_sca_w	bigint;
	
	C01 CURSOR(	nr_seq_proposta_online_pc	pls_proposta_online.nr_sequencia%type) FOR
		SELECT	coalesce(b.ie_preco_vidas_contrato, 'N') ie_preco_vidas_contrato,
			coalesce(b.ie_calculo_vidas, 'A') ie_calculo_vidas,
			a.nr_seq_tabela,
			substr(pls_obter_dados_produto(a.nr_seq_plano, 'N'), 1, 255) nm_sca
		from	pls_sca_vinculo a,
			pls_tabela_preco b
		where	b.nr_sequencia = a.nr_seq_tabela
		and	a.nr_seq_proposta_online = nr_seq_proposta_online_pc;
	
	
BEGIN

	for r_c01_w in c01(nr_seq_proposta_online_p) loop
		begin
		
		vl_sca_w	:= 0;
		
		if (r_c01_w.ie_preco_vidas_contrato = 'S') then
			qt_vidas_sca_w	:= pls_resumo_prop_online_pck.obter_qt_vidas_proposta(nr_seq_proposta_online_p, r_c01_w.ie_calculo_vidas);
		else
			qt_vidas_sca_w	:= 0;
		end if;
		
		select	coalesce(max(vl_preco_atual), 0)
		into STRICT	vl_sca_w
		from	pls_plano_preco
		where	nr_seq_tabela = r_c01_w.nr_seq_tabela
		and	qt_idade_p between qt_idade_inicial and qt_idade_final
		and	((substr(ie_grau_titularidade,1,1) = ie_tipo_benef_p) or (coalesce(ie_grau_titularidade::text, '') = ''))
		and	((r_c01_w.ie_preco_vidas_contrato = 'S' and
			  qt_vidas_sca_w between coalesce(qt_vidas_inicial, qt_vidas_sca_w) and coalesce(qt_vidas_final, qt_vidas_sca_w)) or (r_c01_w.ie_preco_vidas_contrato = 'N'));
		
		CALL CALL pls_resumo_prop_online_pck.add_item(r_c01_w.nm_sca, vl_sca_w, nr_seq_benef_p, '15');
		end;
	end loop;
	
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
