CREATE OR REPLACE FUNCTION pls_segurado_preco_pck.obter_valor_com_desconto ( vl_preco_p bigint, tx_desconto_p bigint, vl_desconto_p bigint) RETURNS bigint AS $body$
DECLARE

vl_retorno_w	double precision;

BEGIN
vl_retorno_w	:= vl_preco_p;
if (tx_desconto_p > 0) then
	vl_retorno_w	:= vl_retorno_w - (vl_retorno_w * tx_desconto_p / 100);
end if;

if (vl_retorno_w = 0) then
	return 0;
else
	return vl_retorno_w - coalesce(vl_desconto_p,0);
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
