CREATE OR REPLACE PROCEDURE pls_segurado_preco_pck.obter_valor ( nr_seq_tabela_p pls_segurado_valor.nr_seq_tabela%type, dt_preco_p pls_segurado_valor.dt_preco%type, qt_idade_p bigint, ie_tipo_parentesco_p pls_segurado_valor.ie_tipo_parentesco%type, ie_tabela_qt_vidas_p pls_segurado_valor.ie_tabela_qt_vidas%type, qt_vidas_p pls_segurado_valor.qt_vidas%type, nr_seq_plano_preco_p INOUT pls_plano_preco.nr_sequencia%type, vl_preco_atual_p INOUT pls_plano_preco.vl_preco_atual%type, vl_preco_nao_subsid_p INOUT pls_plano_preco.vl_preco_nao_subsidiado%type, vl_adaptacao_p INOUT pls_plano_preco.vl_adaptacao%type, vl_minimo_p INOUT pls_plano_preco.vl_minimo%type, tx_acrescimo_p INOUT pls_plano_preco.tx_acrescimo%type) AS $body$
DECLARE

			

nr_seq_plano_preco_w		pls_plano_preco.nr_sequencia%type;
tx_acrescimo_w			pls_plano_preco.tx_acrescimo%type;
vl_preco_atual_w		pls_plano_preco.vl_preco_atual%type;
vl_preco_nao_subsidiado_w	pls_plano_preco.vl_preco_nao_subsidiado%type;
vl_adaptacao_w			pls_plano_preco.vl_adaptacao%type;
vl_minimo_w			pls_plano_preco.vl_minimo%type;

C01 CURSOR(	nr_seq_tabela_pc	pls_segurado_valor.nr_seq_tabela%type,
		qt_idade_pc		bigint,
		ie_tipo_parentesco_pc	grau_parentesco.ie_tipo_parentesco%type) FOR
	SELECT	a.nr_sequencia nr_seq_plano_preco,
		coalesce(a.vl_preco_atual,0) vl_preco_atual,
		coalesce(a.vl_preco_nao_subsid_atual,0) vl_preco_nao_subsidiado,
		coalesce(a.vl_minimo,0) vl_minimo,
		coalesce(a.vl_adaptacao,0) vl_adaptacao,
		coalesce(a.tx_acrescimo,0) tx_acrescimo,
		(	SELECT	min(x.nr_sequencia)
			from	pls_reajuste_preco x
			where	x.nr_seq_preco = a.nr_sequencia
			and	x.dt_reajuste > dt_preco_p) nr_seq_reajuste_preco
	from	pls_plano_preco a
	where	a.nr_seq_tabela	= nr_seq_tabela_pc
	and	qt_idade_pc between a.qt_idade_inicial and a.qt_idade_final
	and (a.ie_grau_titularidade = ie_tipo_parentesco_pc or coalesce(a.ie_grau_titularidade::text, '') = '')
	and	(((ie_tabela_qt_vidas_p = 'S') and qt_vidas_p between coalesce(a.qt_vidas_inicial,qt_vidas_p) and coalesce(a.qt_vidas_final,qt_vidas_p))
	or (ie_tabela_qt_vidas_p = 'N'))
	order	by	coalesce(a.ie_grau_titularidade,' ');

BEGIN

for r_c01_w in C01(nr_seq_tabela_p,qt_idade_p,ie_tipo_parentesco_p) loop
	begin
	if (r_c01_w.nr_seq_reajuste_preco IS NOT NULL AND r_c01_w.nr_seq_reajuste_preco::text <> '') then
		select	vl_base,
			vl_preco_nao_subsid_base,
			vl_adaptacao_base,
			vl_minimo
		into STRICT	vl_preco_atual_w,
			vl_preco_nao_subsidiado_w,
			vl_adaptacao_w,
			vl_minimo_w
		from	pls_reajuste_preco
		where	nr_sequencia	= r_c01_w.nr_seq_reajuste_preco;
	else
		vl_preco_atual_w		:= r_c01_w.vl_preco_atual;
		vl_preco_nao_subsidiado_w	:= r_c01_w.vl_preco_nao_subsidiado;
		vl_adaptacao_w			:= r_c01_w.vl_adaptacao;
		vl_minimo_w			:= r_c01_w.vl_minimo;
	end if;
	
	nr_seq_plano_preco_w	:= r_c01_w.nr_seq_plano_preco;
	tx_acrescimo_w		:= r_c01_w.tx_acrescimo;
	end;
end loop; --C01
vl_preco_atual_p	:= coalesce(vl_preco_atual_w,0);
vl_preco_nao_subsid_p	:= coalesce(vl_preco_nao_subsidiado_w,0);
vl_adaptacao_p		:= coalesce(vl_adaptacao_w,0);
vl_minimo_p		:= coalesce(vl_minimo_w,0);
tx_acrescimo_p		:= coalesce(tx_acrescimo_w,0);
nr_seq_plano_preco_p	:= coalesce(nr_seq_plano_preco_w,0);

END;

$body$
