CREATE OR REPLACE FUNCTION pls_sib_geracao_pck.consultar_sib_regra_opcional ( nr_seq_campo_p bigint) RETURNS varchar AS $body$
DECLARE

qt_registro_w	bigint;
ie_exibir_w	varchar(1);

BEGIN
ie_exibir_w := 'S';

select	count(1)
into STRICT	qt_registro_w
from	pls_sib_regra_opcional
where	cd_campo = nr_seq_campo_p
and	clock_timestamp() between coalesce(dt_inicio_vigencia,clock_timestamp()) and coalesce(dt_fim_vigencia,clock_timestamp())
and	ie_nao_enviar_sib = 'S';

if (qt_registro_w > 0) then
	ie_exibir_w := 'N';
end if;

return ie_exibir_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
