CREATE OR REPLACE PROCEDURE pls_sib_geracao_pck.gerar_ordenacao ( nr_seq_lote_p pls_sib_movimento.nr_seq_lote%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE

nr_ordem_w		pls_sib_regra_ordenacao.nr_ordem%type := 0;

C01 CURSOR FOR
	SELECT	a.nr_ordem nr_seq_ordem_arquivo,
		a.ie_tipo_movimento,
		a.ie_titularidade
	from	pls_sib_regra_ordenacao	a
	where	a.cd_estabelecimento	= cd_estabelecimento_p
	order by a.nr_ordem;

BEGIN

for r_c01_w in c01 loop
	begin
	nr_ordem_w	:= r_c01_w.nr_seq_ordem_arquivo;

	update	pls_sib_movimento	a
	set	a.nr_seq_ordem_arquivo	= nr_ordem_w
	where	a.nr_seq_lote		= nr_seq_lote_p
	and	coalesce(a.nr_seq_ordem_arquivo::text, '') = ''
	and	a.ie_tipo_movimento	= coalesce(r_c01_w.ie_tipo_movimento,a.ie_tipo_movimento)
	and	(r_c01_w.ie_titularidade = 'A' or exists(SELECT	1
							from	pls_segurado	x
							where	x.nr_sequencia	= a.nr_seq_segurado
							and	((r_c01_w.ie_titularidade = 'T' and coalesce(x.nr_seq_titular::text, '') = '') or (r_c01_w.ie_titularidade = 'D' and (x.nr_seq_titular IS NOT NULL AND x.nr_seq_titular::text <> '')))));
	end;
end loop;

update	pls_sib_movimento
set	nr_seq_ordem_arquivo	= nr_ordem_w + 1
where	nr_seq_lote		= nr_seq_lote_p
and	coalesce(nr_seq_ordem_arquivo::text, '') = '';

commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
