							nm_usuario,
							dt_atualizacao_nrec,
							nm_usuario_nrec)
					values (	nextval('pls_sib_mov_consistencia_seq'),
							current_setting('pls_sib_geracao_pck.tb_nr_seq_movimento_w')::pls_util_cta_pck.t_number_table(i),
							current_setting('pls_sib_geracao_pck.tb_nr_seq_consistencia_w')::pls_util_cta_pck.t_number_table(i),
