CREATE OR REPLACE PROCEDURE pls_sib_geracao_pck.remover_inconsistencia ( nr_seq_movimento_p pls_sib_movimento.nr_sequencia%type, cd_inconsistencia_p pls_sib_inconsistencia.cd_inconsistencia%type) AS $body$
DECLARE


nr_seq_sib_mov_consistencia_w		pls_sib_mov_consistencia.nr_sequencia%type;


BEGIN
begin
	select	max(a.nr_sequencia)
	into STRICT	nr_seq_sib_mov_consistencia_w
	from	pls_sib_mov_consistencia a,
		pls_sib_inconsistencia b
	where	b.nr_sequencia	= a.nr_seq_consistencia
	and	a.nr_seq_movimento = nr_seq_movimento_p
	and	b.cd_inconsistencia = cd_inconsistencia_p;
exception
when others then
	nr_seq_sib_mov_consistencia_w := null;
end;

if (nr_seq_sib_mov_consistencia_w IS NOT NULL AND nr_seq_sib_mov_consistencia_w::text <> '') then
	update	pls_sib_mov_consistencia
	set	ie_status = 2
	where	nr_sequencia = nr_seq_sib_mov_consistencia_w;
end if;
END;

