CREATE OR REPLACE PROCEDURE pls_sib_geracao_pck.set_nm_beneficiario ( nm_beneficiario_p pls_sib_segurado.nm_beneficiario%type, nm_beneficiario_ant_p pls_sib_segurado.nm_beneficiario%type, ie_alteracao_p INOUT text) AS $body$
