	else
		current_setting('pls_sib_geracao_pck.tb_nm_beneficiario_w')::pls_util_cta_pck.t_varchar2_table_255(current_setting('pls_sib_geracao_pck.indice_w')::bigint) := trim(both pls_sib_dados_benef_pck.nm_beneficiario);
	end if;
	current_setting('pls_sib_geracao_pck.tb_nm_beneficiario_ant_w')::pls_util_cta_pck.t_varchar2_table_255(current_setting('pls_sib_geracao_pck.indice_w')::bigint) := nm_beneficiario_ant_p;
