CREATE OR REPLACE PROCEDURE pls_simulacao_preco_pck.finalizar_simulacao_preco ( nr_seq_simulacao_p pls_simulacao_preco.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

	
	ie_tipo_contratacao_w		pls_simulacao_preco.ie_tipo_contratacao%type;
	
	C01 CURSOR(	nr_seq_simulacao_pc	pls_simulacao_preco.nr_sequencia%type) FOR
		SELECT	b.nr_seq_tabela,
			b.nr_seq_plano,
			a.nr_sequencia nr_seq_simulacao_perfil,
			a.tx_indice
		from	pls_simulacao_perfil a,
			pls_simul_perfil_tabela b
		where	a.nr_sequencia		= b.nr_seq_simul_perfil
		and	a.nr_seq_simulacao	= nr_seq_simulacao_pc;
	
	C02 CURSOR(	nr_seq_simulacao_pc	pls_simulacao_preco.nr_sequencia%type) FOR
		SELECT	a.nr_seq_tabela,
			a.nr_seq_plano
		from	pls_simulacao_preco_plano a
		where	a.nr_seq_simulacao	= nr_seq_simulacao_pc;
	
	
BEGIN
	
	select	max(ie_tipo_contratacao)
	into STRICT	ie_tipo_contratacao_w
	from	pls_simulacao_preco
	where	nr_sequencia = nr_seq_simulacao_p;
	
	if (ie_tipo_contratacao_w not in ('I')) then
		for r_c01_w in c01(nr_seq_simulacao_p) loop
			begin
			if	(r_c01_w.nr_seq_tabela IS NOT NULL AND r_c01_w.nr_seq_tabela::text <> '' AND r_c01_w.nr_seq_plano IS NOT NULL AND r_c01_w.nr_seq_plano::text <> '') then
				CALL pls_simulacao_preco_pck.inserir_simulacao_resumo(nr_seq_simulacao_p, r_c01_w.nr_seq_simulacao_perfil, r_c01_w.nr_seq_plano,
						r_c01_w.nr_seq_tabela, nm_usuario_p);
				
				if (coalesce(r_c01_w.tx_indice,1) <> 1) then
					update	pls_plano_preco
					set	vl_preco_atual	= vl_preco_atual * r_c01_w.tx_indice,
						nm_usuario	= nm_usuario_p,
						dt_atualizacao	= clock_timestamp()
					where	nr_seq_tabela	= r_c01_w.nr_seq_tabela;
				end if;
			end if;
			end;
		end loop;
	else
		for r_c02_w in c02(nr_seq_simulacao_p) loop
			begin
			CALL pls_simulacao_preco_pck.inserir_simulacao_resumo(nr_seq_simulacao_p, null, r_c02_w.nr_seq_plano, r_c02_w.nr_seq_tabela, nm_usuario_p);
			end;
		end loop;
	end if;
	
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
