SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_sip_pck.acerto_internacao ( nr_seq_lote_p pls_lote_sip.nr_sequencia%type, nr_seq_item_sip_p sip_lote_item_assistencial.nr_seq_item_sip%type, qt_diferenca_p integer, ie_tipo_contratacao_p sip_lote_item_assistencial.ie_tipo_contratacao%type, ie_segmentacao_sip_p sip_lote_item_assistencial.ie_segmentacao_sip%type, sg_uf_p sip_lote_item_assistencial.sg_uf%type, dt_ocorrencia_p sip_lote_item_assistencial.dt_ocorrencia%type) AS $body$
DECLARE


nr_seq_lote_atu_w	sip_lote_item_assistencial.nr_sequencia%type;WITH RECURSIVE cte AS (

				
c01 CURSOR(	nr_seq_item_pc	sip_item_assistencial.nr_sequencia%type)	FOR
	SELECT	nr_sequencia nr_seq_item_sip,ds_item,1 as level
	from	sip_item_assistencial WHERE nr_sequencia = nr_seq_item_pc
  UNION ALL

				
c01 CURSOR(	nr_seq_item_pc	sip_item_assistencial.nr_sequencia%type)	FOR 
	SELECT	nr_sequencia nr_seq_item_sip,ds_item,(c.level+1)
	from	sip_item_assistencial JOIN cte c ON (c.prior nr_sequencia = nr_seq_superior)

) SELECT * FROM cte WHERE level = 2;
;
BEGIN

for r_c01_w in c01(nr_seq_item_sip_p) loop

	-- busca qualquer filho que tenha registro para atualizar a quantidade de eventos

	select	min(xy.nr_sequencia)
	into STRICT	nr_seq_lote_atu_w
	from 	sip_lote_item_assistencial xy
	where 	xy.nr_seq_lote = nr_seq_lote_p
	-- procura o item

	and	xy.nr_seq_item_sip = r_c01_w.nr_seq_item_sip
	and	xy.ie_tipo_contratacao = ie_tipo_contratacao_p
	and	xy.ie_segmentacao_sip = ie_segmentacao_sip_p
	and 	xy.sg_uf = sg_uf_p
	and	xy.dt_ocorrencia = dt_ocorrencia_p
	-- acha um que pode ser alterado

	and (xy.qt_evento + qt_diferenca_p) > 0;
	
	if (nr_seq_lote_atu_w IS NOT NULL AND nr_seq_lote_atu_w::text <> '') then
		
		-- altera a quantidade para nao dar inconsistencia 12 - Numero de eventos nao pode ser diferente do informado nos subitens.

		update	sip_lote_item_assistencial set
			qt_evento = (qt_evento + qt_diferenca_p)
		where	nr_sequencia = nr_seq_lote_atu_w;
		commit;
		
		CALL pls_sip_pck.acerto_internacao(	nr_seq_lote_p, r_c01_w.nr_seq_item_sip, qt_diferenca_p, ie_tipo_contratacao_p,
					ie_segmentacao_sip_p, sg_uf_p, dt_ocorrencia_p);
		-- sai fora depois de acertar a primeira estrutura de filhos		

		exit;
	end if;
	
end loop;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
