CREATE OR REPLACE PROCEDURE pls_sip_pck.ajuste_periodo_3_usjrp (nr_seq_lote_sip_p pls_lote_sip.nr_sequencia%type) AS $body$
DECLARE


nr_seq_periodo_w  pls_lote_sip.nr_sequencia%type;

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	sip_nv_dados
	where	nr_seq_protocolo in (791062, 784664);


c02 CURSOR(nr_seq_nv_dados_p   sip_nv_dados.nr_sequencia%type) FOR
	SELECT	nr_sequencia
	from	sip_nv_vinc_incon_sist
	where	nr_seq_nv_dados = nr_seq_nv_dados_p;
	
BEGIN

select	max(nr_seq_periodo)
into STRICT	nr_seq_periodo_w
from	pls_lote_sip
where	nr_sequencia = nr_seq_lote_sip_p;

if (nr_seq_periodo_w = 3) then
	for r_c01_w in c01 loop
		for r_c02_w in c02(r_C01_w.nr_sequencia) loop
			delete
			from	sip_nv_vinc_incon_sist
			where	nr_sequencia = r_c02_w.nr_sequencia;
		end loop;
	commit;
	delete
	from	sip_nv_dados
	where	nr_sequencia = r_c01_w.nr_sequencia;
	commit;
	end loop;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
