CREATE OR REPLACE PROCEDURE pls_sip_pck.atualiza_envio_indice_exp ( tb_nr_seq_item_benef_p INOUT dbms_sql.number_table, tb_ie_envio_sip_p INOUT dbms_sql.varchar2_table, ie_commit_p text, ie_esvaziar_table_p text) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Atualiza o ie_envico_sip do indice de exposto
	
	Apenas envia para o banco a alteracao,  nao teve possuir nenhuma regra de 
	negocio nesta rotina
	
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------

Pontos de atencao:

Alteracoes:
-------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
	

BEGIN

-- Manda pro banco

forall i in tb_nr_seq_item_benef_p.first..tb_nr_seq_item_benef_p.last
	update	sip_nv_benef_exp_item
	set	ie_envio_sip	= tb_ie_envio_sip_p(i)
	where	nr_sequencia	= tb_nr_seq_item_benef_p(i);
	
-- se foi marcado para commitar	

if (coalesce(ie_commit_p, 'N') = 'S') then

	commit;
end if;

-- se deve esvaziar a tabela

if (coalesce(ie_esvaziar_table_p, 'S') = 'S') then

	tb_nr_seq_item_benef_p.delete;
	tb_ie_envio_sip_p.delete;
end if;


END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
