CREATE OR REPLACE PROCEDURE pls_sip_pck.atualiza_ie_situacao ( nr_seq_incon_sist_p sip_nv_vinc_incon_sist.nr_seq_incon_sist%type, nr_seq_nv_dados_p sip_nv_dados.nr_sequencia%type, nr_sequencia_p sip_nv_vinc_incon_sist.nr_sequencia%type, ie_situacao_p sip_nv_vinc_incon_sist.ie_situacao%type, nm_usuario_p text) AS $body$
BEGIN
	-- se tem sequencia, faz o update direto

	if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
		update	sip_nv_vinc_incon_sist
		set 	ie_situacao 	= ie_situacao_p,
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_sequencia	= nr_sequencia_p;
	else
		-- senao vai pela uk

		update	sip_nv_vinc_incon_sist
		set 	ie_situacao 		= ie_situacao_p,
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp()
		where	nr_seq_incon_sist	= nr_seq_incon_sist_p
		and	nr_seq_nv_dados		= nr_seq_nv_dados_p;
	end if;
	commit;

END;

