-- Atualiza o tempo de geracao da regra



CREATE OR REPLACE PROCEDURE pls_sip_pck.atualiza_tempo_regra ( nr_sequencia_p sip_nv_tempo_regra.nr_sequencia%type, ie_inicio_fim_p text, nm_usuario_p usuario.nm_usuario%type) AS $body$
BEGIN

if (ie_inicio_fim_p = 'FIM') then
	
	update	sip_nv_tempo_regra
	set	dt_fim		= clock_timestamp(),
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_sequencia_p;
	commit;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
