CREATE OR REPLACE PROCEDURE pls_sip_pck.excluir_carencia_incid_exp ( tb_nr_seq_exp_car_p INOUT dbms_sql.number_table, ie_commit_p text, ie_esvaziar_table_p text) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Exclui as carencias passadas por parametro

	Apenas a exclusao delas deve ser feito aqui, regras sobre quais excluir deve
	ficar sob responsabilidade da rotina que chamar esta.
	
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------

Pontos de atencao:

Alteracoes:
-------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


BEGIN

-- Exclui

forall i in tb_nr_seq_exp_car_p.first..tb_nr_seq_exp_car_p.last
	delete	FROM w_sip_exp_carencia
	where	nr_sequencia	= tb_nr_seq_exp_car_p(i);

-- Se foi marcado para commitar	

if (coalesce(ie_commit_p, 'N') = 'S') then

	commit;
end if;

-- Se foi marcado para esvaziar a tabela virtual

if (coalesce(ie_esvaziar_table_p, 'S') = 'S') then

	tb_nr_seq_exp_car_p.delete;
end if;


END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
