CREATE OR REPLACE PROCEDURE pls_sip_pck.grava_carencia_incidente_exp ( tb_nr_seq_tipo_carencia_p INOUT pls_util_cta_pck.t_number_table, nr_seq_lote_sip_p pls_lote_sip.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, ie_commit_p text, ie_esvaziar_table_p text) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Grava as carencias que de alguma forma serao incidentes no periodo do lote SIP
	
	Apenas sera feito a gravacao dos dados, nao deve ser incluso nenhuma regra de 
	negocio aqui
	
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------

Pontos de atencao:

Alteracoes:
-------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


BEGIN

-- Grava

forall i in tb_nr_seq_tipo_carencia_p.first..tb_nr_seq_tipo_carencia_p.last
	insert into w_sip_exp_carencia(	nr_sequencia,
						nr_seq_tipo_carencia,
						nr_seq_lote,
						dt_atualizacao,
						dt_atualizacao_nrec,
						nm_usuario,
						nm_usuario_nrec)
	values (	nextval('w_sip_exp_carencia_seq'),
			tb_nr_seq_tipo_carencia_p(i),
			nr_seq_lote_sip_p,
			clock_timestamp(),
			clock_timestamp(),
			nm_usuario_p,
			nm_usuario_p);
			

-- Se for marcado para commitar

if (coalesce(ie_commit_p,'N') = 'S') then

	commit;
end if;

-- Se for marcado para limpar a tabela virtual

if (coalesce(ie_esvaziar_table_p, 'S') = 'S') then

	tb_nr_seq_tipo_carencia_p.delete;
end if;



END;

$body$
