CREATE OR REPLACE PROCEDURE pls_sip_pck.grava_indice_exposto ( tb_exposicao_p INOUT t_exposicao_benef, nm_usuario_p usuario.nm_usuario%type, ie_commit_p text, ie_esvaziar_tabela_p text) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Grava os indices de exposicao do beneficiario
	
	Os indices sao apenas inseridos nesta rotina, nao deve ser incluso regra de negocio
	aqui.
	
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------

Pontos de atencao:

Alteracoes:
-------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */



BEGIN

-- joga para o banco

forall i in tb_exposicao_p.nr_seq_segurado.first..tb_exposicao_p.nr_seq_segurado.last
	insert into sip_nv_benef_exp_item(	nr_sequencia,
						nr_seq_lote,
						nr_seq_segurado,
						nr_seq_tipo_carencia,
						nr_seq_item_assist,
						qt_exposicao_benef,
						qt_dias_carencia,
						qt_dias_exposicao,
						ie_envio_sip,
						ie_origem_exposicao,
						dt_atualizacao,
						dt_atualizacao_nrec,
						nm_usuario,
						nm_usuario_nrec,
						nr_seq_carencia)
	values (	nextval('sip_nv_benef_exp_item_seq'),
			tb_exposicao_p.nr_seq_lote(i),
			tb_exposicao_p.nr_seq_segurado(i),
			tb_exposicao_p.nr_seq_tipo_carencia(i),
			tb_exposicao_p.nr_seq_item_assist(i),
			tb_exposicao_p.qt_exposicao(i),
			tb_exposicao_p.qt_dias_carencia_lote(i),
			tb_exposicao_p.qt_dias_exposicao(i),
			tb_exposicao_p.ie_envio_sip(i),
			tb_exposicao_p.ie_origem_exposicao(i),
			clock_timestamp(),
			clock_timestamp(),
			nm_usuario_p,
			nm_usuario_p,
			tb_exposicao_p.nr_seq_carencia(i));
			
-- se foi marcado para comitar

if (coalesce(ie_commit_p, 'N') = 'S') then

	commit;
end if;
		
-- se foi marcado para esvaziar as tabelas

if (coalesce(ie_esvaziar_tabela_p, 'S') = 'S') then

	tb_exposicao_p := pls_sip_pck.limpar_tb_exposicao(tb_exposicao_p);
end if;


END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
