CREATE OR REPLACE PROCEDURE pls_sip_pck.inativar_sip ( nr_seq_lote_p pls_lote_sip.nr_sequencia%type, nm_usuario_p text, cd_estabelecimento_p pls_outorgante.cd_estabelecimento%type) AS $body$
BEGIN
	-- faz uma verificacao para ter certeza que o SIP pode ser alterado

	CALL pls_sip_pck.valida_pode_alterar_sip(nr_seq_lote_p, cd_estabelecimento_p, 'somente_estabelecimento');
	
