SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION pls_sip_pck.obter_conta_princ_inter ( nr_seq_segurado_p pls_conta.nr_seq_segurado%type, cd_guia_referencia_p pls_conta.cd_guia_referencia%type, ie_somente_intercambio_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_conta_princ_w	pls_conta.nr_sequencia%type;


BEGIN

-- busca a guia de resumo de internacao

select	min(t.nr_sequencia)
into STRICT	nr_seq_conta_princ_w
from (	SELECT	a.nr_sequencia
	from	pls_conta				a
	where	a.nr_seq_segurado			= nr_seq_segurado_p
	and	a.cd_guia_ok				= cd_guia_referencia_p
	and	a.ie_tipo_guia				= 5
	and	a.ie_status				not in ('D', 'M') -- mesmo status que alimenta a sip_nv_dados
	and	coalesce(ie_somente_intercambio_p, 'N')	= 'N'
	
union all

	SELECT	a.nr_sequencia
	from	pls_conta				a
	where	a.nr_seq_segurado			= nr_seq_segurado_p
	and	a.cd_guia_ok				= cd_guia_referencia_p
	and	a.ie_tipo_guia				= 5
	and	a.ie_status				not in ('D', 'M') -- mesmo status que alimenta a sip_nv_dados
	and	a.ie_origem_conta			= 'A'
	and	coalesce(ie_somente_intercambio_p, 'N')	= 'S' ) t;


-- se nao encontrou nada busca sem considerar tipo de guia

if (coalesce(nr_seq_conta_princ_w::text, '') = '') then

	select	min(t.nr_sequencia)
	into STRICT	nr_seq_conta_princ_w
	from (	SELECT	a.nr_sequencia
		from	pls_conta		a
		where	a.nr_seq_segurado	= nr_seq_segurado_p
		and	a.cd_guia_ok		= cd_guia_referencia_p
		and	a.ie_status		not in ('D', 'M') -- mesmo status que alimenta a sip_nv_dados
		and	coalesce(ie_somente_intercambio_p, 'N')	= 'N'
		
union all

		SELECT	a.nr_sequencia
		from	pls_conta		a
		where	a.nr_seq_segurado	= nr_seq_segurado_p
		and	a.cd_guia_ok		= cd_guia_referencia_p
		and	a.ie_origem_conta	= 'A'
		and	a.ie_status		not in ('D', 'M') -- mesmo status que alimenta a sip_nv_dados
		and	coalesce(ie_somente_intercambio_p, 'N')	= 'S') t;
end if;


-- se nao encontrou nada busca sem considerar tipo de guia e status (formato "padrao" anterior a adicao de status, mantido para compatibilidade)

if (coalesce(nr_seq_conta_princ_w::text, '') = '') then

	select	min(t.nr_sequencia)
	into STRICT	nr_seq_conta_princ_w
	from (	SELECT	a.nr_sequencia
		from	pls_conta		a
		where	a.nr_seq_segurado	= nr_seq_segurado_p
		and	a.cd_guia_ok		= cd_guia_referencia_p
		and	coalesce(ie_somente_intercambio_p, 'N')	= 'N'
		
union all

		SELECT	a.nr_sequencia
		from	pls_conta		a
		where	a.nr_seq_segurado	= nr_seq_segurado_p
		and	a.cd_guia_ok		= cd_guia_referencia_p
		and	a.ie_origem_conta	= 'A'
		and	coalesce(ie_somente_intercambio_p, 'N')	= 'S') t;
end if;

return nr_seq_conta_princ_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON FUNCTION pls_sip_pck.obter_conta_princ_inter ( nr_seq_segurado_p pls_conta.nr_seq_segurado%type, cd_guia_referencia_p pls_conta.cd_guia_referencia%type, ie_somente_intercambio_p text) FROM PUBLIC;
