CREATE OR REPLACE FUNCTION pls_sip_pck.obter_idade ( dt_nascimento_p timestamp, dt_atual_p timestamp, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


-- ie_opcao_p:

--	A	-	Anos

--	M	- 	Meses da idade total

--	DIA	-	Dias da idade total


ds_idade_w		bigint;
dt_atual_w		timestamp;
dt_nascimento_w		timestamp;
qt_anos_w		integer	:= 0;
qt_meses_w		bigint	:= 0;
qt_meses_ww		bigint	:= 0;


BEGIN
-- se nao foi informado data

if (coalesce(dt_atual_p::text, '') = '') then
	dt_atual_w := clock_timestamp();
else
	dt_atual_w := dt_atual_p;
end if;

if (coalesce(dt_nascimento_p::text, '') = '') then
	dt_nascimento_w	:= clock_timestamp();
else
	dt_nascimento_w	:= dt_nascimento_p;
end if;

qt_meses_w := trunc(months_between(dt_atual_w, dt_nascimento_w));

if (qt_meses_w > 11) then
	qt_anos_w := trunc(qt_meses_w / 12);
end if;

qt_meses_ww := qt_meses_w;

if (ie_opcao_p = 'A') then
	ds_idade_w := qt_anos_w;
	
elsif (ie_opcao_p = 'M') then
	ds_idade_w := qt_meses_ww;
	
elsif (ie_opcao_p = 'DIA') then
	ds_idade_w := dt_atual_w - dt_nascimento_w;
end if;

return ds_idade_w;
	
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
