CREATE OR REPLACE PROCEDURE pls_sip_pck.obter_intervalo_inter ( dados_regra_p pls_sip_pck.c_regra_item, nr_seq_item_ini_p INOUT sip_item_assistencial.nr_sequencia%type, nr_seq_item_fim_p INOUT sip_item_assistencial.nr_sequencia%type) AS $body$
BEGIN
-- faz essas validacoes porque dentro das subclassificacoes da internacao nao podem existir itens vinculados a mais de um item assistencial

-- isso significa, por exemplo, dentro de Regime de internacao e seus filhos nao pode conter o mesmo item vinculado em mais de um filho ou no proprio item

-- Regime de internacao

if (dados_regra_p.sequ_item between 83 and 87) then
	nr_seq_item_ini_p := 83;
	nr_seq_item_fim_p := 87;
	
-- causas selecionadas de internacao

elsif (dados_regra_p.sequ_item between 88 and 107) then
	nr_seq_item_ini_p := 88;
	nr_seq_item_fim_p := 107;
	
elsif (dados_regra_p.sequ_item = 108) then
	nr_seq_item_ini_p := 108;
	nr_seq_item_fim_p := 108;
end if;

END;

$body$
