SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION pls_sip_pck.obter_qt_inconsist_ativa ( cd_inconsistencia_p sip_inconsistencia_sistema.cd_inconsistencia%type) RETURNS bigint AS $body$
DECLARE


qt_inconsist_ativa_w	integer;


BEGIN
-- inicia como zero

qt_inconsist_ativa_w := 0;

-- se foi passado algo entao verifica senao ja retorna zero

if (cd_inconsistencia_p IS NOT NULL AND cd_inconsistencia_p::text <> '') then

	select 	count(1)
	into STRICT	qt_inconsist_ativa_w
	from	sip_inconsistencia_sistema a
	where 	cd_inconsistencia = cd_inconsistencia_p
	and	ie_situacao = 'A';
end if;

-- retorna a quantidade de ativos

return qt_inconsist_ativa_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
