SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION pls_sip_pck.obter_se_reembolso_glosado ( nr_seq_conta_p pls_conta.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


vl_itens_w		pls_conta_proc.vl_liberado%type;
ie_glosa_conta_w	pls_conta.ie_glosa%type;
ds_retorno_w		varchar(1);


BEGIN
ds_retorno_w := 'N';


-- verifica se nos itens existe algum valor liberado

select	sum(coalesce(t.vl_liberado,0))
into STRICT	vl_itens_w
from (	SELECT	sum(coalesce(a.vl_liberado,0)) vl_liberado
	from	pls_conta_proc	a
	where	a.nr_seq_conta	= nr_seq_conta_p
	and	a.ie_status	!= 'D'
	
union all

	SELECT	sum(coalesce(a.vl_liberado,0)) vl_liberado
	from	pls_conta_mat	a
	where	a.nr_seq_conta	= nr_seq_conta_p
	and	a.ie_status	!= 'D') t;

-- se nao tiver nenhum valor liberado nos itens nao cancelados, ja considera que a conta esta glosada

if (vl_itens_w = 0) then

	ds_retorno_w := 'S';
else
	-- se encontrou algum valor, ainda olha o ie_glosa da conta esta marcado, por preucoucao

	select	max(coalesce(a.ie_glosa,'N'))
	into STRICT	ds_retorno_w
	from	pls_conta	a
	where	a.nr_sequencia	= nr_seq_conta_p;
end if;


return coalesce(ds_retorno_w, 'N');
	
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
